import React, { Component } from 'react';
import './styles.css';
import { podcastsData, episodesData } from '../pages/data';
import EpisodeList from './EpisodeList';


class PodcastComponent extends Component {
  render() {
    const { podcasts } = this.props;

    return (
      <body>
        <main>
          <section className="episode-list">
            <h2>Épisodes</h2>
            <ul>
              {podcasts.map(podcast => (
                // Utilisez le composant Episode ici en passant le chemin de l'audio
                <EpisodeList
                  key={podcast.id}
                  title={podcast.title}
                  description={podcast.description}
                  audioSrc={podcast.audioSrc} // Ajoutez cette propriété
                />
              ))}
            </ul>
          </section>

          <section className="subscribe">
            <h2>S'Abonner</h2>
            <p>Abonnez-vous à notre podcast pour ne manquer aucun épisode passionnant.</p>
          </section>
        </main>
      </body>
    );
  }
}

export default PodcastComponent;
