// Home.js
import React from 'react';
import { Route, Routes, Link, useNavigate } from 'react-router-dom';
import PodcastList from '../components/EpisodeList';
import PodcastDetails from '../components/PodcastDetails';
import Header from '../components/header';
import Footer from '../components/footer';
import { podcastsData, episodesData } from './data';

const Home = () => {
  const navigate = useNavigate();

  const handlePodcastClick = (podcastId) => {
    navigate(`/podcasts/${podcastId}`);
  };

  return (
    <div className="wrapper">
      <Header />

      <Routes>
        <Route
          path="/"
          element={<PodcastList podcasts={podcastsData} onPodcastClick={handlePodcastClick} />}
        />
        <Route
          path="/podcasts/:podcastId"
          element={<PodcastDetails podcasts={podcastsData} episodes={episodesData} />}
        />
      </Routes>

      <Footer />
    </div>
  );
};

export default Home;
