// PodcastComponent.js
import React, { useState } from 'react';

const PodcastComponent = ({ podcasts, episodes, match }) => {
  // Vérifiez si match existe avant d'accéder à match.params
  const podcastId = match?.params?.podcastId;
  
  const selectedPodcast = podcasts.find(podcast => podcast.id === podcastId);

  const [currentEpisode, setCurrentEpisode] = useState(null);

  const playEpisode = episodeId => {
    setCurrentEpisode(episodeId);
  };

  if (!selectedPodcast) {
    return <div>Podcast non trouvé</div>;
  }

  return (
    <div>
      <img src={selectedPodcast.image} alt={`Cover of ${selectedPodcast.title}`} />
      <h2>{selectedPodcast.title}</h2>
      <p>{selectedPodcast.description}</p>

      <h3>Épisodes</h3>
      {selectedPodcast.episodes.map(episodeId => {
        const episode = episodes.find(e => e.id === episodeId);
        return (
          <div key={episode.id}>
            <p>{episode.date}</p>
            <h4>{episode.title}</h4>
            <p>{episode.description}</p>
            <button onClick={() => playEpisode(episode.id)}>
              Lire l'épisode
            </button>
            {currentEpisode === episode.id && (
              <audio controls>
                <source src={episode.audio} type="audio/mpeg" />
                Votre navigateur ne supporte pas l'audio HTML5.
              </audio>
            )}
          </div>
        );
      })}
    </div>
  );
};

export default PodcastComponent;
