// EpisodeList.js
import React from 'react';
import { useParams } from 'react-router-dom';

function EpisodeList({ podcasts }) {
  const { podcastId } = useParams();
  const selectedPodcast = podcasts.find((podcast) => podcast.id === parseInt(podcastId, 10));

  if (!selectedPodcast) {
    return <div>Podcast non trouvé</div>;
  }

  return (
    <div>
      <h2>Liste des Épisodes pour le Podcast {selectedPodcast.title}</h2>
      <ul>
        {selectedPodcast.episodes.map((episode) => (
          <li key={episode.id}>
            <h3>{episode.title}</h3>
            <p>{episode.description}</p>
            <div><audio controls>
          {episode.audioSrc}
         
          Votre navigateur ne supporte pas l'audio HTML5.
        </audio></div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default EpisodeList;
