import React from 'react';
import './styles.css';
import { podcastsData, episodesData } from '../pages/data';

class PodcastComponent extends Component {
  render() {
    return (
      <body>
        <main>
          <section className="episode-list">
            <h2>Épisodes</h2>
            <ul>
              {podcastsData.map(podcast => (
                <li key={podcast.id}>
                  {/* Utilisez la structure des données pour afficher les épisodes */}
                  <img src="episode1.jpg" alt={`Épisode ${podcast.id}`} />
                  <h3>{podcast.title}</h3>
                  <p>{podcast.description}</p>
                  {/* Utilisez la structure des données pour afficher les balises audio */}
                  {podcast.episodes.map(episode => (
                    <audio controls key={episode.id}>
                      <source src={episode.audioUrl} type="audio/mpeg" />
                      Votre navigateur ne supporte pas l'audio HTML5.
                    </audio>
                  ))}
                </li>
              ))}
            </ul>
          </section>

          <section className="subscribe">
            <h2>S'Abonner</h2>
            <p>Abonnez-vous à notre podcast pour ne manquer aucun épisode passionnant.</p>
          </section>
        </main>
      </body>
    );
  }
}

export default PodcastComponent;
